			s1++;
		if(a[n-i]<mid&&a[n-i-1]<mid)
			s0++;
		if(a[n+i]>=mid&&a[n+i+1]>=mid)
			s1++;
		if(a[n+i]<mid&&a[n+i+1]<mid)
			s0++;
		if(s1)
			return 1;
		if(s0)
			return 0;
	}
