    data.assign(n, 0);
  }
  void add(int i, long long x) {
    i++;
    while(i<=n){
      data[i-1] += x;
      i += i&-i;
    }
  }
  void set(int i, long long x) {
    add(i, x - get(i));
  }
  long long sum(int i) {
    assert(i < n);
    if(i<0) return 0;
    i++;
    long long s = 0;
    while(i>0){
      s += data[i-1];
      i -= i&-i;
    }
    return s;
  }
  long long get(int i) {
    return sum(i) - (i ? sum(i - 1) : 0);
  }
  long long range(int a, int b) {
    return sum(b) - sum(a-1);
  }
};

