bool is_eq_greater(int val)
{
    vector<int> v(2 * N - 1);
    REP(i, 2 * N - 1) v[i] = (a[i] >= val);
    {
        bool flg = true;
        REP(i, N) if (!v[i]) flg = false;
        if (flg) return true;
    }
    if (!v[N - 1] and !v[N]) return false;
    if (!v[N - 1] and !v[N - 2]) return false;
    int r2 = N, l2 = N, r1 = 0, l1 = 0;
    REP(k, N - 1)
    {
        if (v[N - 1 + k] + v[N - 1 + k + 1] == 0) break;
        r1++;
        if (v[N - 1 + k] + v[N - 1 + k + 1] == 2) { r2 = k; break; }
    }
    REP(k, N - 1)
    {
        if (v[N - 1 - k] + v[N - 1 - k - 1] == 0) break;
        l1++;
        if (v[N - 1 - k] + v[N - 1 - k - 1] == 2) { l2 = k; break; }
    }
    if (r2 < l1) return true;
    if (l2 < r1) return true;
    return false;
}


