     * @brief
     * begin iterator
     * @return I&
     */
    I& begin() { return i; }
    /**
     * @brief
     * end iterator
     * @return I&
     */
    I& end() { return n; }
    /**
     * @brief
     * range(reverse_ranges)
     * @return reverse_range
     */
    reverse_range operator!() { return reverse_range(*i, *n); }
};
