 * @tparam T range-based for 
 * @tparam U 
 * @param v 
 * @param delimiter 
 * @return std::string delimiter
 */
template <typename T, typename U>
std::string join(const T& v, const U delimiter) {
    std::stringstream ss;
    bool isFirst = true;
    for (auto& it : v) {
        if (!isFirst) {
            ss << delimiter;
        }
        isFirst = false;
        ss << it;
    }
    return ss.str();
}

/**
 * @brief 
 * @tparam ITR 
 * @tparam T 
 * @param bg 
 * @param ed 
 * @param delimiter 
 * @return std::string delimiter
 */
template <typename ITR, typename T>
std::string join(const ITR bg, const ITR ed, const T delimiter) {
    std::stringstream ss;
    bool isFirst = true;
    for (auto it = bg; it != ed; ++it) {
        if (!isFirst) {
            ss << delimiter;
        }
        isFirst = false;
        ss << *it;
    }
    return ss.str();
}
