const int maxn = 200010;
using namespace std;
int n, a[maxn], b[maxn], h[2];
bool ok(int ave) {
	rep(i, 1, 2 * n - 1) a[i] = (b[i] >= ave);
	if (a[n] == a[n - 1] || a[n] == a[n + 1]) return a[n];
	int l = n, r = n;
	while (l - 1 && a[l - 1] != a[l]) -- l;
	while (r + 1 <= n * 2 - 1 && a[r + 1] != a[r]) ++ r;
	if ((l + r) % 2) {
		if (n - l < r - n) -- r;
			else ++ l;
	}
	if ((l + r) / 2 < n) return a[r + 1];
	if ((l + r) / 2 > n) return a[l - 1];
	h[0] = h[1] = 0;
	++ h[a[n] ^ ((n - l) & 1)];
	if (l - 1) ++ h[a[l - 1]];
	if (r + 1 <= n * 2 - 1) ++ h[a[r + 1]];
	return (h[1] > h[0]);
}
int main() {
	scanf("%d", &n);
	rep(i, 1, 2 * n - 1) scanf("%d", &b[i]);
	int l = 1, r = 2 * n - 1, ans;
	while (l <= r) {
