	for (int i = 0; i < n - 1; i++)
	{
		if ((b[pos - i] && b[pos - i - 1]) || (b[pos + i] && b[pos + i + 1]))
			return 1;
		if ((!b[pos - i] && !b[pos - i - 1]) || (!b[pos + i] && !b[pos + i + 1]))
			return 0;
	}
