		a1[n+1]=get_center(a[n+1-1],a[n+1],a[n+1+1]);
		if ((a1[n]==a1[n-1])||(a1[n]==a1[n+1])){
			return a1[n];
		}
		for (w=2;w<n-h+1;++w){
			a1[n-w]=get_center(a[n-w-1],a[n-w],a[n-w+1]);
			a1[n+w]=get_center(a[n+w-1],a[n+w],a[n+w+1]);
		}
		for (w=0;w<n-h+1;++w){
			a[n-w]=a1[n-w];
			a[n+w]=a1[n+w];
		}
	}

	return a1[n];
}

