int n;

int calc(int arr[]){
    int i,lp=-1,rp=-1;
    for(i=n;i>=2;i--){
        if(arr[i-1]==arr[i]){lp=i;break;}
    }
    for(i=n;i<=2*n-2;i++){
        if(arr[i]==arr[i+1]){rp=i;break;}
    }
    if(lp==-1&&rp==-1){
        return (arr[n]+(n-1))%2;
    }
    if(lp==-1){return arr[rp];}
    if(rp==-1){return arr[lp];}
    if(arr[rp]==arr[lp]){return arr[rp];}
    if((rp-n)<(n-lp)){return arr[rp];}
    else{return arr[lp];}
}

