import java.util.InputMismatchException;
import java.util.Queue;

public class Main {
	static InputStream is;
	static PrintWriter out;
	static String INPUT = "";

	static void solve()
	{
		int n = ni();
		int[] a = na(2*n-1);
		Queue<Integer> q = new ArrayDeque<>();
		for(int i = 0;i+2 < 2*n-1;i++){
			if(a[i] < a[i+1] && a[i+1] < a[i+2])continue;
			if(a[i] > a[i+1] && a[i+1] > a[i+2])continue;
			q.add(i+1);
		}
		int[] temp = new int[2*n-1];
		boolean[] done = new boolean[2*n-1];
		for(int i = 0;i < n-1;i++){
			Queue<Integer> nq = new ArrayDeque<>();
			for(int cur : q){
				temp[cur] = mid(a[cur-1], a[cur], a[cur+1]);
			}
			for(int cur : q){
				a[cur] = temp[cur];
				for(int j = cur-1;j <= cur+1;j++){
					if(i+2 <= j && j <= 2*n-1-i-2 && !done[j]){
						if(a[j-1] <= a[j] && a[j] <= a[j+1])continue;
						if(a[j-1] >= a[j] && a[j] >= a[j+1])continue;
						nq.add(j);
						done[j] = true;
					}
				}
			}
			for(int cur : nq)done[cur] = false;
			q = nq;
		}
		out.println(a[n-1]);
	}

	static int mid(int a, int b, int c)
	{
		if(a <= b){
			if(b <= c){
				return b;
			}else if(a >= c){
				return a;
			}else{
				return c;
			}
		}else{
			if(b >= c){
				return b;
			}else if(a <= c){
				return a;
			}else{
				return c;
			}
		}
	}

	public static void main(String[] args) throws Exception
	{
		long S = System.currentTimeMillis();
		is = INPUT.isEmpty() ? System.in : new ByteArrayInputStream(INPUT.getBytes());
		out = new PrintWriter(System.out);

		solve();
		out.flush();
		long G = System.currentTimeMillis();
		tr(G-S+"ms");
	}

	private static boolean eof()
	{
		if(lenbuf == -1)return true;
		int lptr = ptrbuf;
		while(lptr < lenbuf)if(!isSpaceChar(inbuf[lptr++]))return false;

		try {
			is.mark(1000);
			while(true){
				int b = is.read();
				if(b == -1){
					is.reset();
					return true;
				}else if(!isSpaceChar(b)){
					is.reset();
					return false;
				}
			}
		} catch (IOException e) {
			return true;
		}
	}

	private static byte[] inbuf = new byte[1024];
	static int lenbuf = 0, ptrbuf = 0;

	private static int readByte()
	{
		if(lenbuf == -1)throw new InputMismatchException();
		if(ptrbuf >= lenbuf){
			ptrbuf = 0;
			try { lenbuf = is.read(inbuf); } catch (IOException e) { throw new InputMismatchException(); }
			if(lenbuf <= 0)return -1;
		}
		return inbuf[ptrbuf++];
	}

	private static boolean isSpaceChar(int c) { return !(c >= 33 && c <= 126); }
//	private static boolean isSpaceChar(int c) { return !(c >= 32 && c <= 126); }
	private static int skip() { int b; while((b = readByte()) != -1 && isSpaceChar(b)); return b; }

	private static double nd() { return Double.parseDouble(ns()); }
	private static char nc() { return (char)skip(); }

	private static String ns()
	{
		int b = skip();
		StringBuilder sb = new StringBuilder();
		while(!(isSpaceChar(b))){
			sb.appendCodePoint(b);
			b = readByte();
		}
		return sb.toString();
	}

	private static char[] ns(int n)
	{
		char[] buf = new char[n];
		int b = skip(), p = 0;
		while(p < n && !(isSpaceChar(b))){
			buf[p++] = (char)b;
			b = readByte();
		}
		return n == p ? buf : Arrays.copyOf(buf, p);
	}

	private static char[][] nm(int n, int m)
	{
		char[][] map = new char[n][];
		for(int i = 0;i < n;i++)map[i] = ns(m);
		return map;
	}

	private static int[] na(int n)
	{
		int[] a = new int[n];
		for(int i = 0;i < n;i++)a[i] = ni();
		return a;
	}

	private static int ni()
	{
		int num = 0, b;
		boolean minus = false;
		while((b = readByte()) != -1 && !((b >= '0' && b <= '9') || b == '-'));
		if(b == '-'){
			minus = true;
			b = readByte();
		}

		while(true){
			if(b >= '0' && b <= '9'){
				num = num * 10 + (b - '0');
			}else{
				return minus ? -num : num;
			}
			b = readByte();
		}
	}

	private static long nl()
	{
		long num = 0;
		int b;
		boolean minus = false;
		while((b = readByte()) != -1 && !((b >= '0' && b <= '9') || b == '-'));
		if(b == '-'){
			minus = true;
			b = readByte();
		}

		while(true){
			if(b >= '0' && b <= '9'){
				num = num * 10 + (b - '0');
			}else{
				return minus ? -num : num;
			}
			b = readByte();
		}
	}

	private static void tr(Object... o) { if(INPUT.length() != 0)System.out.println(Arrays.deepToString(o)); }
}

import java.io.*;
import java.util.*;

public class Main {
    public static void main(String[] args) {
        PrintWriter out = new PrintWriter(System.out);

        Scanner sc = new Scanner(System.in);

        int N = sc.nextInt();
        int[] a = new int[2 * N - 1];
        for (int i = 0; i < a.length; ++i) {
            a[i] = sc.nextInt();
        }

        int high = 2 * N - 1;
        int low = 0;
        int[] b = new int[a.length];
        while ((high - low) > 1) {
            int mid = (high + low) / 2;
            for (int i = 0; i < a.length; ++i) {
                b[i] = a[i] <= mid ? 0 : 1;
            }
            if (go(b)) {
                high = mid;
            } else {
                low = mid;
            }
        }
        out.println(high);
        out.flush();
    }
    private static boolean go(int[] b) {
        int len = b.length;
        int[] tmp = new int[len];
        while (len > 1) {
            len -= 2;
            for (int i = 0; i < len; ++i) {
                tmp[i] = b[i] + b[i + 1] + b[i + 2];
                if (tmp[i] <= 1) {
                    tmp[i] = 0;
                } else {
                    tmp[i] = 1;
                }
            }
            b = tmp;
        }
        return b[0] == 0;
    }
    private static int mid(int a1, int a2, int a3) {
        if (a1 <= a2 && a2 <= a3) {
            return a2;
        } else if (a2 <= a1 && a1 <= a3) {
            return a1;
        } else {
            return a3;
        }
    }
}

