/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 *
 * @author Denis Nedelyaev
 */
public class Main {
    public static void main(String[] args) throws Exception {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        FastScanner in = new FastScanner(inputStream);
        PrintWriter out = new PrintWriter(outputStream);
        TaskD solver = new TaskD(in, out);
        solver.solve(1);
        out.close();
    }

    static class TaskD {
        private final FastScanner in;
        private final PrintWriter out;

        public TaskD(FastScanner in, PrintWriter out) {
            this.in = in;
            this.out = out;
        }

        public void solve(int testNumber) {
            int n = in.nextInt();
            int[] a = in.nextInts(2 * n - 1);
            int left = 0;
            int right = a.length;
            int[] alts = new int[a.length];
            int altCount = 0;
            boolean[] isAlt = new boolean[a.length];

            for (int i = 1; i + 1 < a.length; i++) {
                if ((a[i] - a[i - 1] < 0) != (a[i + 1] - a[i] < 0)) {
                    alts[altCount++] = i;
                    isAlt[i] = true;
                }
            }

            int[] toChange = new int[a.length];
            int[] toChangePos = new int[a.length];
            int[] temp = new int[3];

            while (left + 1 < right) {
                int toChangeCount = 0;

                for (int j = 0; j < altCount; j++) {
                    int i = alts[j];
                    isAlt[i] = false;
                    temp[0] = a[i - 1];
                    temp[1] = a[i];
                    temp[2] = a[i + 1];
                    Arrays.sort(temp);
                    toChange[toChangeCount] = temp[1];
                    toChangePos[toChangeCount] = i;
                    toChangeCount++;
                }

                for (int i = 0; i < toChangeCount; i++) {
                    a[toChangePos[i]] = toChange[i];
                }

                altCount = 0;

                left++;
                right--;

                for (int i = 0; i < toChangeCount; i++) {
                    int j = toChangePos[i];
                    for (int k = j - 1; k < j + 2; k++) {
                        if (k > left && k + 1 < right && !isAlt[k]) {
                            if ((a[k] - a[k - 1] < 0) != (a[k + 1] - a[k] < 0)) {
                                alts[altCount++] = k;
                                isAlt[k] = true;
                            }
                        }
                    }
                }
            }

            out.println(a[left]);
        }

    }

    static class FastScanner {
        private final static int BUFFER_SIZE = 4096;
        private final InputStream in;
        private byte[] buffer = new byte[BUFFER_SIZE];
        private int pos = 0;
        private int size;

        public FastScanner(InputStream inputStream) throws IOException {
            in = inputStream;
            size = 0;
        }

        public int nextInt() {
            int c = skipWhitespace();

            int sign = -1;
            if (c == '-') {
                sign = 1;
                c = read();
            }

            int ans = 0;

            while (c > ' ') {
                ans *= 10;
                ans -= c - '0';
                c = read();
            }

            return sign * ans;
        }

        public int[] nextInts(int n) {
            int[] res = new int[n];
            for (int i = 0; i < n; i++) {
                res[i] = nextInt();
            }
            return res;
        }

        private int skipWhitespace() {
            while (true) {
                int c = read();
                if (c > ' ' || c == -1) {
                    return c;
                }
            }
        }

        private int read() {
            if (pos >= size) {
                try {
                    size = in.read(buffer, 0, BUFFER_SIZE);
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
                if (size <= 0) {
                    return -1;
                }
                pos = 0;
            }
            return buffer[pos++];
        }

    }
}

import java.io.IOException;
import java.io.InputStream;
import java.util.NoSuchElementException;


public class Main {

	public static void main(String[] args) {
		FastScanner sc = new FastScanner();
		int N = sc.nextInt();
		int[] a = sc.nextIntList(2 * N - 1);

		int left = 0;
		int right = 200000;
		while (left <= right) {
			int k = left + right >>> 1;
			if (check(a, k)) {
				left = k + 1;
			} else {
				right = k - 1;
			}
		}
		System.out.println(right);
	}

	private static boolean check (int[] a, int k) {
		int N  = a.length / 2;
		int n = a.length;

		int cnt1 = -1;
		boolean flg1 = false;
		for (int i = N; i < n - 1; i ++) {
			boolean b1 = a[i] >= k;
			boolean b2 = a[i + 1] >= k;
			if (b1 == b2) {
				flg1 = b1;
				cnt1 = n - i - 1;
				break;
			}
		}

		int cnt2 = -1;
		boolean flg2 = false;
		for (int i = N; i >= 1; i --) {
			boolean b1 = a[i] >= k;
			boolean b2 = a[i - 1] >= k;
			if (b1 == b2) {
				flg2 = b1;
				cnt2 = i;
				break;
			}
		}

		if (cnt1 < 0 && cnt2 < 0) {
			int x = a[N] >= 0 ? 1 : 0;
			return (x + N) % 2 == 1;
		} else {
			return cnt2 < cnt1 ? flg1 : flg2;
		} 
	}
}



class FastScanner {
	public static String debug = null;

	private final InputStream in = System.in;
	private int ptr = 0;
	private int buflen = 0;
	private byte[] buffer = new byte[1024];
	private boolean eos = false;

	private boolean hasNextByte() {
		if (ptr < buflen) {
			return true;
		} else {
			ptr = 0;
			try {
				if (debug != null) {
					buflen = debug.length();
					buffer = debug.getBytes();
					debug = "";
					eos = true;
				} else {
					buflen = in.read(buffer);
				}
			} catch (IOException e) {
				e.printStackTrace();
			}
			if (buflen < 0) {
				eos = true;
				return false;
			} else if (buflen == 0) {
				return false;
			}
		}
		return true;
	}

	private int readByte() {
		if (hasNextByte())
			return buffer[ptr++];
		else
			return -1;
	}

	private static boolean isPrintableChar(int c) {
		return 33 <= c && c <= 126;
	}

	private void skipUnprintable() {
		while (hasNextByte() && !isPrintableChar(buffer[ptr]))
			ptr++;
	}

	public boolean isEOS() {
		return this.eos;
	}

	public boolean hasNext() {
		skipUnprintable();
		return hasNextByte();
	}

	public String next() {
		if (!hasNext())
			throw new NoSuchElementException();
		StringBuilder sb = new StringBuilder();
		int b = readByte();
		while (isPrintableChar(b)) {
			sb.appendCodePoint(b);
			b = readByte();
		}
		return sb.toString();
	}

	public long nextLong() {
		if (!hasNext())
			throw new NoSuchElementException();
		long n = 0;
		boolean minus = false;
		int b = readByte();
		if (b == '-') {
			minus = true;
			b = readByte();
		}
		if (b < '0' || '9' < b) {
			throw new NumberFormatException();
		}
		while (true) {
			if ('0' <= b && b <= '9') {
				n *= 10;
				n += b - '0';
			} else if (b == -1 || !isPrintableChar(b)) {
				return minus ? -n : n;
			} else {
				throw new NumberFormatException();
			}
			b = readByte();
		}
	}

	public int nextInt() {
		return (int) nextLong();
	}

	public long[] nextLongList(int n) {
		return nextLongTable(1, n)[0];
	}

	public int[] nextIntList(int n) {
		return nextIntTable(1, n)[0];
	}

	public long[][] nextLongTable(int n, int m) {
		long[][] ret = new long[n][m];
		for (int i = 0; i < n; i ++) {
			for (int j = 0; j < m; j ++) {
				ret[i][j] = nextLong();
			}
		}
		return ret;
	}

	public int[][] nextIntTable(int n, int m) {
		int[][] ret = new int[n][m];
		for (int i = 0; i < n; i ++) {
			for (int j = 0; j < m; j ++) {
				ret[i][j] = nextInt();
			}
		}
		return ret;
	}
}

