            TaskB solver = new TaskB();
            solver.solve(1, in, out);
            out.close();
        }
    }

    static class TaskB {
        public void solve(int testNumber, FastInput in, FastOutput out) {
            int n = in.readInt();
            int[] dep1 = new int[n * 2];
            for (int i = 1; i < 2 * n; i++) {
                dep1[i] = in.readInt();
            }

            int[] dep2 = new int[n * 2];
            for (int i = 2; i < n * 2 - 1; i++) {
                dep2[i] = CompareUtils.middleOf(dep1[i - 1], dep1[i], dep1[i + 1]);
            }

            out.println(dep2[n]);
        }

    }

    static class FastInput {
        private final InputStream is;
        private byte[] buf = new byte[1 << 13];
        private int bufLen;
        private int bufOffset;
        private int next;

        public FastInput(InputStream is) {
            this.is = is;
        }

        private int read() {
            while (bufLen == bufOffset) {
                bufOffset = 0;
                try {
                    bufLen = is.read(buf);
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
                if (bufLen == -1) {
                    return -1;
                }
            }
            return buf[bufOffset++];
        }

        public void skipBlank() {
            while (next >= 0 && next <= 32) {
                next = read();
            }
        }

        public int readInt() {
            int sign = 1;

            skipBlank();
            if (next == '+' || next == '-') {
                sign = next == '+' ? 1 : -1;
                next = read();
            }

            int val = 0;
            if (sign == 1) {
                while (next >= '0' && next <= '9') {
                    val = val * 10 + next - '0';
                    next = read();
                }
            } else {
                while (next >= '0' && next <= '9') {
                    val = val * 10 - next + '0';
                    next = read();
                }
            }

            return val;
        }

    }

    static class FastOutput implements AutoCloseable, Closeable {
        private StringBuilder cache = new StringBuilder(1 << 20);
        private final Writer os;

        public FastOutput(Writer os) {
            this.os = os;
        }

        public FastOutput(OutputStream os) {
            this(new OutputStreamWriter(os));
        }

        public FastOutput println(int c) {
            cache.append(c).append('\n');
            return this;
        }

        public FastOutput flush() {
            try {
                os.append(cache);
                cache.setLength(0);
            } catch (IOException e) {
                throw new UncheckedIOException(e);
            }
            return this;
        }

        public void close() {
            flush();
            try {
                os.close();
            } catch (IOException e) {
                throw new UncheckedIOException(e);
            }
        }

    }

    static class CompareUtils {
        private CompareUtils() {
        }

        public static int middleOf(int a, int b, int c) {
            if (a == b || a == c) {
                return a;
            }
            if (b == c) {
                return b;
            }
            int signAB = Integer.signum(a - b);
            int signAC = Integer.signum(a - c);
            int signBC = Integer.signum(b - c);
            if (signAB != signAC) {
                return a;
            }
            if (signAB == signBC) {
                return b;
            }
            return c;
        }

    }
}

import java.util.Arrays;
import java.util.Comparator;
import java.util.List;
import java.util.Scanner;

public class Main {
	public static void main(String[] args) {
		Scanner scanner = new Scanner(System.in);
		int n = scanner.nextInt();

		int length = n * 2 - 1;
		int[] nums = new int[length];
		for (int i = 0; i < length; i++) {
			nums[i] = scanner.nextInt();
		}

		for (int i = 0;i < n - 1; i++) {
			nums = center(nums);
		}
		System.out.println(nums[0]);
	}

	public static int[] center(int[] nums){
		int [] nextNums = new int[nums.length - 2];
		for (int i = 0; i < nextNums.length; i++) {
			nextNums[i] = center(nums[i], nums[i + 1], nums[i + 2]);
		}
		return nextNums;
	}

	public static int center(int a,int b,int c){
		List<Integer> integers = Arrays.asList(a,b,c);
		integers.sort(Comparator.naturalOrder());
		return integers.get(1);
	}
}

import java.io.ByteArrayInputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.PrintWriter;
import java.util.Arrays;
import java.util.InputMismatchException;

public class Main {
	static InputStream is;
	static PrintWriter out;
	static String INPUT = "";

	static void solve()
	{
		int n = ni();
		int[] a = na(2*n-1);
		int low = 1, high = 2*n-1;
		while(high - low > 1){
			int h = high+low>>1;
			if(go(a, h)){
				low = h;
			}else{
				high = h;
			}
		}
		out.println(low);
	}

	static boolean go(int[] a, int h)
	{
		int n = a.length;
		boolean[] b = new boolean[n];
		for(int i = 0;i < n;i++){
			b[i] = a[i] >= h;
		}
		int start = -1;
		for(int i = 1;i < n;i++){
			if(b[i] != b[i-1]){
				if(start == -1)start = i-1;
			}else{
				if(start != -1){
					if((i-start)%2 == 1){
						for(int j = start;j <= i;j++){
							b[j] = b[i];
						}
					}else{
						for(int j = start;j < start + (i-start)/2;j++){
							b[j] = b[start];
						}
						for(int j = start+(i-start)/2;j <= i;j++){
							b[j] = b[i];
						}
					}
				}
				start = -1;
			}
		}
		if(start != -1){
			int i = n;
			if((i-start)%2 == 1){
				for(int j = start;j < i;j++){
					b[j] = b[start];
				}
			}else{
				for(int j = start;j < start + (i-start)/2;j++){
					b[j] = b[start];
				}
				for(int j = start+(i-start)/2;j < i;j++){
					b[j] = !b[start];
				}
			}
			start = -1;
		}
		return b[n/2];
	}

	public static void main(String[] args) throws Exception
	{
		long S = System.currentTimeMillis();
		is = INPUT.isEmpty() ? System.in : new ByteArrayInputStream(INPUT.getBytes());
		out = new PrintWriter(System.out);

		solve();
		out.flush();
		long G = System.currentTimeMillis();
		tr(G-S+"ms");
	}

	private static boolean eof()
	{
		if(lenbuf == -1)return true;
		int lptr = ptrbuf;
		while(lptr < lenbuf)if(!isSpaceChar(inbuf[lptr++]))return false;

		try {
			is.mark(1000);
			while(true){
				int b = is.read();
				if(b == -1){
					is.reset();
					return true;
				}else if(!isSpaceChar(b)){
					is.reset();
					return false;
				}
			}
		} catch (IOException e) {
			return true;
		}
	}

	private static byte[] inbuf = new byte[1024];
	static int lenbuf = 0, ptrbuf = 0;

	private static int readByte()
	{
		if(lenbuf == -1)throw new InputMismatchException();
		if(ptrbuf >= lenbuf){
			ptrbuf = 0;
			try { lenbuf = is.read(inbuf); } catch (IOException e) { throw new InputMismatchException(); }
			if(lenbuf <= 0)return -1;
		}
		return inbuf[ptrbuf++];
	}

	private static boolean isSpaceChar(int c) { return !(c >= 33 && c <= 126); }
//	private static boolean isSpaceChar(int c) { return !(c >= 32 && c <= 126); }
	private static int skip() { int b; while((b = readByte()) != -1 && isSpaceChar(b)); return b; }

	private static double nd() { return Double.parseDouble(ns()); }
	private static char nc() { return (char)skip(); }

	private static String ns()
	{
		int b = skip();
		StringBuilder sb = new StringBuilder();
		while(!(isSpaceChar(b))){
			sb.appendCodePoint(b);
			b = readByte();
		}
		return sb.toString();
	}

	private static char[] ns(int n)
	{
		char[] buf = new char[n];
		int b = skip(), p = 0;
		while(p < n && !(isSpaceChar(b))){
			buf[p++] = (char)b;
			b = readByte();
		}
		return n == p ? buf : Arrays.copyOf(buf, p);
	}

	private static char[][] nm(int n, int m)
	{
		char[][] map = new char[n][];
		for(int i = 0;i < n;i++)map[i] = ns(m);
		return map;
	}

	private static int[] na(int n)
	{
		int[] a = new int[n];
		for(int i = 0;i < n;i++)a[i] = ni();
		return a;
	}

	private static int ni()
	{
		int num = 0, b;
		boolean minus = false;
		while((b = readByte()) != -1 && !((b >= '0' && b <= '9') || b == '-'));
		if(b == '-'){
			minus = true;
			b = readByte();
		}

		while(true){
			if(b >= '0' && b <= '9'){
				num = num * 10 + (b - '0');
			}else{
				return minus ? -num : num;
			}
			b = readByte();
		}
	}

	private static long nl()
	{
		long num = 0;
		int b;
		boolean minus = false;
		while((b = readByte()) != -1 && !((b >= '0' && b <= '9') || b == '-'));
		if(b == '-'){
			minus = true;
			b = readByte();
		}

		while(true){
			if(b >= '0' && b <= '9'){
				num = num * 10 + (b - '0');
			}else{
				return minus ? -num : num;
			}
			b = readByte();
		}
	}

	private static void tr(Object... o) { if(INPUT.length() != 0)System.out.println(Arrays.deepToString(o)); }
}

