    private static boolean valid(int x) {
        int pp = -1,p = -1,m = N/2;
        for (int i = 0;i < N;i++){
            A[i] = 0;
            if (P[i] >= x) A[i] = 1;
        }
        for (int i = 0;i < N;i++) {
            int v = A[i];
            if (p == v) {
                if (v == 1 && (i == m || i-1 == m)) return true;
                pp = p;
                p = v;
            }
            else {
                if (pp == v) {
                    if (v == 1 && (i-2 == m || i-1 == m || i == m)) return true;
                    pp = p = v;
                }
                else {
                    pp = p;
                    p = v;
                }
            }
        }
        return false;
    }

