	int goFast(int[] a) {
		int len = a.length;
		int mid = len / 2;

		int ans = -1;
		int bestDist = Integer.MAX_VALUE;

		for (int i = 0; i < a.length;) {
			int j = i;
			while (j < a.length && a[i] == a[j]) {
				j++;
			}

			if (j - i > 1) {
				if (i <= mid && mid < j) {
					return a[i];
				}
				int dist = Math.min(Math.abs(mid - (j - 1)), Math.abs(mid - i));
				if (dist < bestDist) {
					bestDist = dist;
					ans = a[i];
				}
			}

			i = j;
		}

		if (ans != -1) {
			return ans;
		}

		return a[0];
	}

	int go(int[] a) {
		while (a.length > 1) {
			int[] b = new int[a.length - 2];
			for (int i = 0; i < b.length; i++) {
				b[i] = a[i] + a[i + 1] + a[i + 2];
				if (b[i] <= 1) {
					b[i] = 0;
				} else {
					b[i] = 1;
				}
			}
			a = b;
		}
		return a[0];
	}

	int solve(int[] perm) {
		int n = perm.length / 2 + 1;

		int high = 2 * n - 1; // ans <= high
		int low = 0; // ans > 0

		while (high - low > 1) {
			int mid = (low + high) >> 1;
			int[] tmp = new int[perm.length];
			for (int i = 0; i < perm.length; i++) {
				tmp[i] = perm[i] <= mid ? 0 : 1;
			}
			// if (go(tmp) == 0) {
			if (goFast(tmp) == 0) {
				high = mid;
			} else {
				low = mid;
			}
		}
		return high;
	}

