	class Problem : IDisposable
	{
		bool IsGCJ;
		int Repeat;
		Scanner sc;
		Printer pr;
		public Problem(bool isGCJ, Scanner scanner, Printer printer)
		{
			sc = scanner;
			pr = printer;
			IsGCJ = isGCJ;
			if (isGCJ) Repeat = sc.Get<int>();
			else Read();
		}
		public Problem(bool isGCJ) : this(isGCJ, new Scanner(), new Printer()) { }
		public Problem(bool isGCJ, Scanner scanner) : this(isGCJ, scanner, new Printer()) { }
		public Problem(bool isGCJ, Printer printer) : this(isGCJ, new Scanner(), printer) { }
		public void Solve()
		{
			if (IsGCJ) for (var i = 0; i < Repeat; i++) { Read(); pr.Write("Case #" + (i + 1) + ": "); SolveOne(); }
			else SolveOne();
		}
		public void Dispose()
		{
			sc.Dispose();
			pr.Dispose();
		}
		public int Size { get { return 1; } }
		public const long Mod = 1000000007;
		RandomSFMT rand = Program.rand;
		int N;
		int[] a;
		void Read()
		{
			sc.Read(out N);
			a = sc.ReadMany<int>();
		}
		int M,C;
		void SolveOne()
		{
			b = new bool[M = 2 * N - 1];
			C = N - 1;
			pr.WriteLine(Func.FirstBinary(2, 2 * N - 1, x => !Hantei(x)) - 1);
		}
		bool[] b;
		bool Hantei(int x)
		{
			for (var i = 0; i < M; i++) b[i] = a[i] >= x;
			var flag = true;
			for (var i = 1; flag && i < M; i++) flag &= b[i] ^ b[i - 1];
			if (flag) return b[0];
			if (b[C] == b[C - 1] || b[C] == b[C + 1]) return b[C];
			int l = C, r = C;
			while (l > 0 && b[l] ^ b[l - 1]) l--;
			while (r < M - 1 && b[r] ^ b[r + 1]) r++;
			l++;
			int m = (l + r) / 2;
			if (C < m) return b[l - 1];
			else return b[r];
		}
	}
}
class BFS01
{
	public static int[] BFS(int V, int from, IEnumerable<Tuple<int,bool>>[] edgeFrom)
	{
		var d = new int[V];
		for (var i = 0; i < V; i++) d[i] = Func.Inf;
		d[from] = 0;
		var deq = new Deque<int>();
		deq.PushFront(from);
		while (deq.Count > 0)
		{
			var u = deq.PopFront();
			foreach (var t in edgeFrom[u])
			{
				int v = t.Item1, cost = t.Item2 ? 1 : 0, tmp = d[u] + cost;
				if (d[v] > tmp)
				{
					d[v] = tmp;
					if (t.Item2) deq.PushBack(v);
					else deq.PushFront(v);
				}
			}
		}
		return d;
	}
}
