	class Problem : IDisposable
	{
		bool IsGCJ;
		int Repeat;
		Scanner sc;
		Printer pr;
		public Problem(bool isGCJ, Scanner scanner, Printer printer)
		{
			sc = scanner;
			pr = printer;
			IsGCJ = isGCJ;
			if (isGCJ) Repeat = sc.Get<int>();
			else Read();
		}
		public Problem(bool isGCJ) : this(isGCJ, new Scanner(), new Printer()) { }
		public Problem(bool isGCJ, Scanner scanner) : this(isGCJ, scanner, new Printer()) { }
		public Problem(bool isGCJ, Printer printer) : this(isGCJ, new Scanner(), printer) { }
		public void Solve()
		{
			if (IsGCJ) for (var i = 0; i < Repeat; i++) { Read(); pr.Write("Case #" + (i + 1) + ": "); SolveOne(); }
			else SolveOne();
		}
		public void Dispose()
		{
			sc.Dispose();
			pr.Dispose();
		}
		public int Size { get { return 1; } }
		public const long Mod = 1000000007;
		RandomSFMT rand = Program.rand;
		int N;
		int[] a;
		void Read()
		{
			sc.Read(out N);
			a = sc.ReadMany<int>();
		}
		void SolveOne()
		{
			var b = new int[2 * N - 3];
			for (var i = 0; i < 2 * N - 3; i++) b[i] = Median(a[i], a[i + 1], a[i + 2]) - 1;
			var c = new int[2 * N - 1];
			var p = new int[2 * N - 1];
			for (var i = 0; i < 2 * N - 1; i++) p[i] = 3 * N;
			for (var i = 0; i < 2 * N - 3; i++)
			{
				var x = b[i];
				c[x]++;
				var d = Math.Abs(i - (N - 2));
				if (d < p[x]) p[x] = d;
			}
			var max = 0;
			for (var i = 0; i < 2 * N - 1; i++)
			{
				if (c[i] > c[max]) max = i;
				else if (c[i] == c[max] && p[i] < p[max]) max = i;
			}
			pr.WriteLine(max + 1);
		}
		int Median(int a, int b, int c)
		{
			if (a > b) { int x = a; a = b; b = x; }
			if (a > c) { int x = a; a = c; c = x; }
			if (b > c) return c;
			else return b;
		}
	}
}
class BFS01
{
	public static int[] BFS(int V, int from, IEnumerable<Tuple<int,bool>>[] edgeFrom)
	{
		var d = new int[V];
		for (var i = 0; i < V; i++) d[i] = Func.Inf;
		d[from] = 0;
		var deq = new Deque<int>();
		deq.PushFront(from);
		while (deq.Count > 0)
		{
			var u = deq.PopFront();
			foreach (var t in edgeFrom[u])
			{
				int v = t.Item1, cost = t.Item2 ? 1 : 0, tmp = d[u] + cost;
				if (d[v] > tmp)
				{
					d[v] = tmp;
					if (t.Item2) deq.PushBack(v);
					else deq.PushFront(v);
				}
			}
		}
		return d;
	}
}
