class Program
{
    public static IO IO = new IO();
    static void Main(string[] args)
    {
        int n = IO.INT();
        int[] m = IO.INT(' ');

        List<int> buf = new List<int>(m);
        while (true)
        {
            int a = 2 * buf.Count - 1;
            if (a == 1)
            {
                break;
            }
            List<int> buf2 = new List<int>();
            for (int i = 1; i < buf.Count - 1; i++)
            {
                buf2.Add(mid(buf[i - 1], buf[i], buf[i + 1]));
            }
            buf = buf2;
        }
        IO.WRITE(buf[0]);
        IO.FLUSH();
    }
    static public int mid(int a, int b, int c)
    {
        int ret = a;
        if (a >= b)
        {
            if (a >= c)
            {
                if (b >= c)
                {
                    ret = b;
                }
                else
                {
                    ret = c;
                }
            }
            else
            {
                ret = a;
            }
        }
        else
        {
            if (a >= c)
            {
                ret = a;
            }
            else
            {
                if (b >= c)
                {
                    ret = c;
                }
                else
                {
                    ret = b;
                }
            }
        }
        return ret;
    }
}

