    bool f(int c) {
        bool[] bs = as.map!(a => a>=c).array;
        foreach(i; 1..N) {
            if (bs[N-1+i-1] == bs[N-1+i]) return bs[N-1+i];
            if (bs[N-1-i+1] == bs[N-1-i]) return bs[N-1-i];
        }
