        let mut last = list.iter().max().cloned().unwrap();
        while last - first > 1 {
            let mid = (first + last) / 2;
            if is_top_gte(&list, mid) {
                first = mid;
            } else {
                last = mid;
            }
        }
        if is_top_gte(&list, first) {
            first
        } else {
            last
        }
    })
    .to_string()
}

// listx
fn is_top_gte(list: &Vec<i64>, x: i64) -> bool {
    let c_i = list.len() / 2;

    // 
    let left = {
        let mut res = None;
        let mut prev = list[c_i] >= x;
        for (d, i) in ((c_i - 1)..=0).enumerate() {
            let cur = list[i] >= x;

            if prev == cur {
                res = Some((d, cur));
                break;
            }
            prev = cur;
        }
        res
    };

    // 
    let right = {
        let mut res = None;
        let mut prev = list[c_i] >= x;
        for (d, i) in ((c_i + 1)..list.len()).enumerate() {
            let cur = list[i] >= x;

            if prev == cur {
                res = Some((d, cur));
                break;
            }
            prev = cur;
        }
        res
    };

    match (left, right) {
        (Some((ld, gte)), Some((rd, _))) if ld < rd => gte,
        (Some(_), Some((_, gte))) => gte,
        (None, Some((_, gte))) => gte,
        (Some((_, gte)), None) => gte,
        (None, None) => {
            if list.len() % 4 == 1 {
                list[c_i] > x
            } else {
                !(list[c_i] > x)
            }
        }
    }
}

