#include <bits/stdc++.h>
using namespace std;

struct LCA{
    static const int BITLEN_MAX = 30;
    vector<int> parent[BITLEN_MAX];
    vector<int> depth;
    int bitlen;

    void initialize(int N, const vector<int> edges[]){
        int root = 0;
        bitlen = 1;
        while((1<<bitlen) < N) bitlen += 1;
        for(int i=0; i<bitlen; i++) parent[i].resize(N);
        depth.resize(N, -1);

        dfs(root, -1, 0, edges);
        for(int k=0; k<bitlen-1; k++){
            for(int v=0; v<N; v++){
                if(depth[v] == -1) continue;
                if(parent[k][v] < 0){
                    parent[k+1][v] = -1;
                }else{
                    parent[k+1][v] = parent[k][parent[k][v]];
                }
            }
        }
    }

    void dfs(int v, int p, int d, const vector<int> edges[]){
        parent[0][v] = p;
        depth[v] = d;
        for(auto u : edges[v]){
            if(u != p) dfs(u, v, d+1, edges);
        }
    }

    int calc_lca(int u, int v){
        if(depth[u] > depth[v]) swap(u, v);
        for(int k=0; k<bitlen; k++){
            if( ((depth[v]-depth[u]) >> k) & 1 ) v = parent[k][v];
        }
        if(u == v) return u;
        for(int k=bitlen-1; k>=0; k--){
            if(parent[k][u] != parent[k][v]){
                u = parent[k][u];
                v = parent[k][v];
            }
        }
        return parent[0][u];
    }

    int calc_dist(int u, int v){
        int l = calc_lca(u, v);
        return depth[u] + depth[v] - depth[l]*2;
    }
};

vector<int> edges[2][200000];
int dist[2][200000];
bitset<200000> dead, eternal;

void dfs(int i, int p, int d, int k){
    dist[k][i] = d;
    for(int j : edges[k][i]) if(j != p) dfs(j, i, d+1, k);
}

int ans = 0;
const int INF = 1e9;
void dfs2(int i, int p){
    if(dead[i]) return;
    ans = max(ans, eternal[i] ? INF : dist[1][i] * 2);
    for(int j : edges[0][i]) if(j != p) dfs2(j, i);
}

int main(){
    int N, S[2];
    cin >> N >> S[0] >> S[1];
    for(int k=0; k<2; k++){
        for(int i=0; i<N-1; i++){
            int a, b;
            cin >> a >> b;
            edges[k][a-1].push_back(b-1);
            edges[k][b-1].push_back(a-1);
        }
        S[k]--;
        dfs(S[k], -1, 0, k);
    }

    for(int i=0; i<N; i++) if(dist[0][i] >= dist[1][i]) dead[i] = true;

    LCA lca1;
    lca1.initialize(N, edges[1]);
    for(int i=0; i<N; i++){
        if(dead[i]) continue;
        for(int j : edges[0][i]){
            if(dead[j]) continue;
            if(lca1.calc_dist(i, j) > 2) eternal[i] = eternal[j] = true;
        }
    }

    dfs2(S[0], -1);
    cout << (ans == INF ? -1 : ans) << endl;
    return 0;
}

