#define INF 1000000000

int N, X, Y;
vector <int> redAdjList[200005], blueAdjList[200005];

int blueDist[200005], p[200005][20];
void root(int u, int prevP, int prevDist)
{
	blueDist[u] = prevDist; p[u][0] = prevP;
	for (int v : blueAdjList[u])
	{
		if (v == prevP) continue;
		root(v, u, prevDist + 1);
	}
}

int getLca(int a, int b)
{
	if (blueDist[a] < blueDist[b]) swap(a, b);
	for (int k = 19; k >= 0; k--)
	{
		if (p[a][k] == -1) continue;
		if (blueDist[p[a][k]] >= blueDist[b]) a = p[a][k];
	}
	if (a == b) return a;
	for (int k = 19; k >= 0; k--)
	{
		if (p[a][k] != p[b][k]) {a = p[a][k]; b = p[b][k];}
	}
	return p[a][0];
}

int getDist(int a, int b)
{
	return blueDist[a] + blueDist[b] - blueDist[getLca(a, b)] * 2;
}

int getMaxDist(int u, int prevP, int prevDist)
{
	int cur = blueDist[u] * 2;
	//printf("Calling getMaxDist(%d, %d, %d); dist: %d\n", u, prevP, prevDist, getDist(u, prevP));
	if (prevP != -1)
	{
		if (prevDist >= blueDist[u]) return 0;
		if (getDist(u, prevP) > 2) return INF;
	}
	for (int v : redAdjList[u])
	{
		if (u == prevP) continue;
		cur = max(cur, getMaxDist(v, u, prevDist + 1));
	}
	return cur;
}

int main()
{
	scanf("%d%d%d", &N, &X, &Y);
	for (int i = 0; i < N - 1; i++)
	{
		int a, b; scanf("%d%d", &a, &b);
		redAdjList[a].push_back(b); redAdjList[b].push_back(a);
	}
	for (int i = 0; i < N - 1; i++)
	{
		int c, d; scanf("%d%d", &c, &d);
		blueAdjList[c].push_back(d); blueAdjList[d].push_back(c);
	}
	root(Y, -1, 0);
	for (int k = 1; k < 20; k++)
	{
		for (int i = 1; i <= N; i++)
		{
			p[i][k] = -1;
			if (p[i][k-1] == -1) continue;
			p[i][k] = p[p[i][k-1]][k-1];
		}
	}
	int ans = getMaxDist(X, -1, 0);
	if (ans == INF) printf("-1\n");
	else printf("%d\n", ans);
	return 0;
}

