#include <bits/stdc++.h>
#define SZ(x) ((int) (x).size())
using namespace std;

typedef long long i64;

const int NMAX = 200005, LOG = 20;
const int INF = 0x3f3f3f3f;

vector<int> T1[NMAX], T2[NMAX];
int father[NMAX], level[NMAX];
int anc[LOG][NMAX];

int dist[NMAX];

void dfs(int node, int prev) {
    father[node] = prev;
    for (int to: T2[node]) {
        if (to != prev) {
            level[to] = level[node] + 1;
            dfs(to, node);
        }
    }
}

int goUp(int x, int lvl) {
    for (int k = LOG - 1; k >= 0; --k) {
        if (lvl & (1 << k)) {
            x = anc[k][x];
        }
    }
    return x;
}

int lca(int x, int y) {
    if (level[y] > level[x]) {
        y = goUp(y, level[y] - level[x]);
    } else if (level[x] > level[y]) {
        x = goUp(x, level[x] - level[y]);
    }
    if (x == y) {
        return x;
    }
    for (int k = LOG - 1; k >= 0; --k) {
        if (anc[k][x] != anc[k][y]) {
            x = anc[k][x];
            y = anc[k][y];
        }
    }
    return father[x];
}

int xdist(int x, int y) {
    return level[x] + level[y] - 2 * level[lca(x, y)];
}

int main() {
    #ifdef LOCAL_RUN
    freopen("task.in", "r", stdin);
    freopen("task.out", "w", stdout);
    //freopen("task.err", "w", stderr);
