    for (int i = 1; i < n; ++i) {
        int x, y;
        cin >> x >> y;
        T1[x].push_back(y);
        T1[y].push_back(x);
    }
    for (int i = 1; i < n; ++i) {
        int x, y;
        cin >> x >> y;
        T2[x].push_back(y);
        T2[y].push_back(x);
    }
    dfs(r2, -1);
    father[r2] = r2;

    for (int i = 1; i <= n; ++i) {
        anc[0][i] = father[i];
    }
    for (int k = 1; k < LOG; ++k) {
        for (int i = 1; i <= n; ++i) {
            anc[k][i] = anc[k - 1][anc[k - 1][i]];
        }
    }

    memset(dist, INF, sizeof dist);
    queue<int> q;
    dist[r1] = 0;
    q.push(r1);
    int ans = 0;
    while (!q.empty()) {
        int node = q.front();
        q.pop();
        ans = max(ans, 2 * level[node]);

        for (int to: T1[node]) {
            if (xdist(node, to) > 2) {
                cout << "-1\n";
                return 0;
            }
            if (dist[to] > dist[node] + 1 && dist[node] + 1 < level[to]) {
                dist[to] = dist[node] + 1;
                q.push(to);
            }
            if (dist[to] > dist[node] + 1 && dist[node] + 1 <= level[to]) {
                ans = max(ans, 2 * level[to]);
            }
        }
    }
    cout << ans << '\n';
}

