const int N=400005;
queue<int > Q;
int a[N],b[N],vis[N],dis[N],n,X,Y,c[N],d[N],flag[N];
struct Tree{
	int ne[N],tot,sd[N],fi[N],zz[N],fa[20][N],out[N],l,in[N];
	void jb(int x,int y){
		ne[++tot]=fi[x];
		fi[x]=tot;
		zz[tot]=y;
	}
	void dfs(int x,int y,int z){
		fa[0][x]=y;sd[x]=z;in[x]=++l;
		for (int i=fi[x];i;i=ne[i])
			if (zz[i]!=y)dfs(zz[i],x,z+1);
		out[x]=++l;	
	}
	int pd(int x,int y){
		if (!x)return 1;
		return in[x]<=in[y]&&out[x]>=out[y];
	}
	int lca(int x,int y){
		if (x==y)return x;
		if (sd[x]<sd[y])swap(x,y);
		for (int i=19;i>=0;i--)
			if (!pd(fa[i][x],y))x=fa[i][x];
		return fa[0][x];
	}
	int dis(int x,int y){
		return sd[x]+sd[y]-2*sd[lca(x,y)];
	}
	void init(){
		for (int i=1;i<=19;i++)
			for (int j=1;j<=n;j++)fa[i][j]=fa[i-1][fa[i-1][j]];
	}
}G1,G2;
