	}
	for (int i=1;i<n;i++){
		scanf("%d%d",&c[i],&d[i]);
		G2.jb(c[i],d[i]);G2.jb(d[i],c[i]);
	}
	G1.dfs(1,0,0);G2.dfs(1,0,0);
	G1.init();G2.init();
	int ans=0;
	for (int i=1;i<n;i++)
		if (G2.dis(a[i],b[i])>=3)flag[a[i]]=flag[b[i]]=1;
	Q.push(X);vis[X]=1;
	while (!Q.empty()){
		int k=Q.front();Q.pop();
		for (int i=G1.fi[k];i;i=G1.ne[i])
			if (!vis[G1.zz[i]]){
				dis[G1.zz[i]]=dis[k]+1;
				if (dis[G1.zz[i]]<G2.dis(G1.zz[i],Y))Q.push(G1.zz[i]),vis[G1.zz[i]]=1;
			}
	}
	for (int i=1;i<=n;i++)
		if (vis[i]){
			if (flag[i])ans=1e9;
			else ans=max(ans,G2.dis(i,Y)*2);
		}
	if (ans==1e9)puts("-1");
	else printf("%d\n",ans);	
}

