    for (auto u : y[v]){
        if (u==p) continue;
        dfsy(u,v,h+1);
    }
}
ll check(ll u,ll v){
    if (par[u]==par[v] || u==par[v] || v==par[u] || v==par[par[u]] || u==par[par[v]]) return 0;
    return 1;
}
ll dp[N];
void dfs(ll v,ll p){
    if (h1[v]>=h2[v]) return ;
    dp[v]=h2[v]-h1[v];
    dp[v]*=2;
    for (auto u : g[v]){
        if (p==u) continue;
        dfs(u,v);
        dp[v]=max(dp[v],dp[u]+2);
        if (check(u,v)) dp[v]=1e9;
    }
}
