int n,s,t,a[N],b[N],d[N],x[N],y[N],z[N],next[N],p[N],f[N],h[N];

void travel(int x){
	z[x]=z[f[x]]+1;
	for (int t=p[x];t;t=next[t])
		if (y[t]!=f[x])
		f[y[t]]=x,travel(y[t]);
}

void solve(int x){
	for (int t=p[x];t;t=next[t])
		if (!d[b[t]]&&d[x]+1<z[b[t]])
		d[b[t]]=d[x]+1,solve(b[t]);
}

void build(int x[],int y[]){
	fill(p+1,p+n+1,0);
	for (int i=1;i<=n-1;i++){
		next[2*i-1]=p[x[2*i-1]];p[x[2*i-1]]=2*i-1;
		next[2*i]=p[x[2*i]];p[x[2*i]]=2*i;
	}
}

