{
	tot++;nxt[tot]=head[a];head[a]=tot;ver[tot]=b;return ;
}
int dis[N];
int f[N][20];
void dfs(int x,int fa)
{
	for(int i=head[x];i;i=nxt[i])
	{
		if(ver[i]==fa)continue;
		dis[ver[i]]=dis[x]+1;
		f[ver[i]][0]=x;
		dfs(ver[i],x);
	}
