int lca(int a,int b)
{
	if(dis[a]<dis[b])swap(a,b);
	for(int i=19;i>=0;i--)
	{
		if(dis[f[a][i]]>=dis[b])a=f[a][i];
	}
	if(a==b)return a;
	for(int i=19;i>=0;i--)
	{
		if(f[a][i]!=f[b][i])
		{
			a=f[a][i];b=f[b][i];
		}
	}
	return f[a][0];
}
int calc(int a,int b)
{
	return dis[a]+dis[b]-2*dis[lca(a,b)];
}
void dffs(int x,int fa,int now)
{
	if(ans!=-1)ans=max(ans,(dis[x]-1)*2);
	for(int i=head[x];i;i=nxt[i])
	{
		if(ver[i]==fa)continue;
		if(dis[ver[i]]<=now+1)continue;
		if(calc(x,ver[i])>=3)
		{
			ans=-1;
