int lca(int u,int v){
	if(h[u]>h[v])swap(u,v);
	for (int i=0;h[v]-h[u];i++)if((1<<i)&(h[v]-h[u])){
		v=pr[i][v];
	}
	if(u==v)return u;
	for (int i=LCA_UP-1;i>=0;i--)if(pr[i][u]!=pr[i][v]){
		v=pr[i][v];
		u=pr[i][u];
	}
	return pr[0][u];
}
