		fa[x][0] = la, dep[x] = dep[la] + 1;
		for(int i = 1; i < 20; i++) fa[x][i] = fa[fa[x][i - 1]][i - 1];
		for(int i = fi[x]; i; i = ne[i]) {
			int u = to[i];
			if(u == la) continue;
			dfs(u, x);
		}
	}

	inline int LCA(int x, int y) {
		if(dep[x] < dep[y]) swap(x, y);
		int k = dep[x] - dep[y];
		for(int i = 0; i < 20; i++)
		 	if(k & (1 << i)) x = fa[x][i];
		if(x == y) return x;
		for(int i = 19; ~i; --i)
			if(fa[x][i] != fa[y][i])
				x = fa[x][i], y = fa[y][i];
		return fa[x][0];
	}

