int n,X,Y,h1[MN+5],h2[MN+5],cnt=0,d1[MN+5],d2[MN+5],q[MN+5],top;
int nl[MN+5],nr[MN+5],dn=0,vis[MN+5],dep[MN+5],fa[MD+1][MN+5],flag,ans=0;
struct edge{int to,next;}e[MN*4+5];
inline void ins(int*H,int f,int t)
{
	e[++cnt]=(edge){t,H[f]};H[f]=cnt;
	e[++cnt]=(edge){f,H[t]};H[t]=cnt;	
}
void Bfs(int*d,int*head,int s)
{
	memset(vis,0,sizeof(vis));
	vis[q[top=1]=s]=1;
	for(int i=1;i<=top;++i)
		for(int j=head[q[i]];j;j=e[j].next)
			if(!vis[e[j].to]) vis[e[j].to]=1,d[q[++top]=e[j].to]=d[q[i]]+1;	
}
void Pre(int x,int f)
{
	fa[0][x]=f;nl[x]=++dn;
	for(int i=h2[x];i;i=e[i].next)
		if(e[i].to!=f) dep[e[i].to]=dep[x]+1,Pre(e[i].to,x);
	nr[x]=dn;
}
inline int GetUp(int x,int k)
{
	for(int j=0;k;k>>=1,++j) if(k&1) x=fa[j][x];
	return x;
}
inline int lca(int x,int y)
{
	if(dep[x]<dep[y]) swap(x,y);
	x=GetUp(x,dep[x]-dep[y]);
	if(x==y) return x;
	for(int i=MD;~i;--i) if(fa[i][x]!=fa[i][y]) x=fa[i][x],y=fa[i][y];
	return fa[0][x];
}
inline int dis(int x,int y){return dep[x]+dep[y]-2*dep[lca(x,y)];}
inline int Move(int x,int y)
{
	if(x==y) return x;
	if(nl[y]>=nl[x]&&nl[y]<=nr[x]) return GetUp(y,dep[y]-dep[x]-1);
	else return fa[0][x];
}
void dfs(int x,int fa,int oth)
{
	ans=max(ans,d1[x]+dis(x,oth));
	if(x==oth) return;
	for(int i=h1[x];i;i=e[i].next)
