int bdistance [maxn], ojciec[maxn], ile[maxn];
vector<int> red[maxn], blue[maxn];

int Find (int x)
{
	if (ojciec[x]!=x)
		ojciec[x]=Find(ojciec[x]);
	return ojciec[x];
}

void join (int a, int b)
{
	a = Find(a);
	b = Find(b);
	if (ile[a] > ile[b])
		swap(a,b);
	if (a != b)
	{
		ojciec[a]=b;
		ile[b] += ile[a];
	}
}

void calculate_blue (int x)
{
	czy[x]=true;
	int s = (int)blue[x].size();
	for (int i = 0; i<s; ++i)
	{
		if(!czy[blue[x][i]])
		{
			bdistance[blue[x][i]] = bdistance[x] + 1;
			calculate_blue(blue[x][i]);
		}
	}
	czy[x]=false;
}

void calculate_special (int x, int y)
{
	czy[x]=true;
	int s = (int)red[x].size();
	for(int i=0; i<s; ++i)
	{
		if (czy[red[x][i]])
		{
			int odl = bdistance[x] + bdistance[red[x][i]] - 2 * bdistance[Find(red[x][i])];
			if (odl >= 3)
				special[x] = special[red[x][i]] = true;
		}
	}
	s = (int) blue[x].size();
	for (int i = 0; i<s; ++i)
	{
		if(!czy[blue[x][i]])
			calculate_special (blue[x][i], x);
	}
	join (x, y);
	czy [x] = false;
}

int answer (int x, int y)
{
	czy [x] = true;
	if (special[x] && bdistance [x] > y)
		return inf;
	if (bdistance [x] <= y)
		return y;
	int odp = bdistance [x];
	int s = (int)red[x].size();
	for (int i = 0; i<s; ++i)
	{
		if (!czy[red[x][i]])
		{
			odp = max(answer (red[x][i], y + 1), odp);
		}
	}
	return odp;	
}


