typedef long long llint;

const int MAX = 200100;
const int LG = 20;

vector<int> E[MAX], F[MAX];

int L[MAX][LG];
int parent[MAX], d[MAX];

int lca(int u, int v) {
  if (d[u] < d[v]) swap(u, v);
  for (int j = LG - 1; j >= 0; --j)
    if (d[u] - (1 << j) >= d[v]) u = L[u][j];
  if (u == v) return u;
  for (int j = LG - 1; j >= 0; --j)
    if (L[u][j] != L[v][j]) u = L[u][j], v = L[v][j];
  return L[u][0];
}

int dist(int x, int y) { return d[x] + d[y] - 2 * d[lca(x, y)]; }

void dfs(int x) {
  for (int y : E[x]) {
    if (y != parent[x]) {
      parent[y] = x;
      d[y] = d[x] + 1;
      dfs(y);
    }
  }
}

int f[MAX];

void dfs2(int x, int dad, int d, int y) {
  int q = dist(x, y);
  f[x] = d;
  if (q <= d) return;

  for (int nx : F[x]) {
    if (nx != dad) {
      dfs2(nx, x, d + 1, y);
    }
  }
}

