    D[x]=D[y]+1;
    if(D[x]>d[x]) return;
    if(wi[x])ans=orz;
    else ans=max(ans,d[x]*2);
    for(int i=0;i<v[x].size();++i)if(v[x][i]!=y)dfs2(v[x][i],x);
}
int main(){
    scanf("%d%d%d",&n,&X,&Y);
    for(int i=1;i<n;++i)scanf("%d%d",x+i,y+i);
    for(int i=1;i<n;++i){
        int x,y;
        scanf("%d%d",&x,&y);
        v[x].pb(y),v[y].pb(x);
    }
    d[0]=-1;
    dfs(Y,0);
    for(int i=1;i<=n;++i)v[i].clear();
    for(int i=1;i<n;++i){
        int o=d[x[i]]+d[y[i]]-d[lca(x[i],y[i])]*2;
        if(o>2)wi[x[i]]=wi[y[i]]=1;
        else v[x[i]].pb(y[i]),v[y[i]].pb(x[i]);
    }
    D[0]=-1;
    dfs2(X,0);
    if(ans==orz)puts("-1");
    else printf("%d\n",ans);
    //system("pause");
    return 0;
}

