void DFS2(int x, int fa){
    dep[x] = dep[fa] + 1; f[x] = fa;
    ff[x][0] = fa;
    for(int i = 1; i <= 20; i++)
        ff[x][i] = ff[ff[x][i - 1]][i - 1];
    for(int i = fi2[x]; i; i = nt2[i]){
        int v = go2[i]; if(v == fa) continue;
        DFS2(v, x); Q[x].push_back(v);
    }
    sort(Q[x].begin(), Q[x].end());
}
int lca(int u, int v)
{
    if(dep[u] < dep[v]) swap(u, v);
    for(int i = 20; i >= 0; i--)
        if(dep[ff[u][i]] >= dep[v]) u = ff[u][i];
    if(u == v) return u;
    for(int i = 20; i >= 0; i--)
    {
        if(ff[u][i] != ff[v][i]) {
            u = ff[u][i]; v = ff[v][i];
        }
    }
    return ff[u][0];
}
int get_dis(int x, int y){
    return dep[x] + dep[y] - dep[lca(x, y)] * 2;
}
void DFS1(int x, int fa, int h){
    bool flag = true, t = false;
    int siz = Q[h].size();
    for(int i = fi1[x]; i; i = nt1[i]){
        int v = go1[i]; if(v == fa) continue;
        if(get_dis(x, v) >= 3) { flag = false; break;}
        int now = lower_bound(Q[h].begin(), Q[h].end(), x) - Q[h].begin();
        if(now < siz && Q[h][now] == v) continue;
        if(v == f[h] || v == h) continue; 
        t = true; DFS1(v, x, f[v]); 
    }
    if(!flag) return;
    if(!t) ans = max(ans, dep[h]); 
}

