struct {
    vector<int> e[N];
    int fa[N][M], d[N];
    void dfs(int u) {
        for (auto v : e[u]) {
            if (v == fa[u][0]) continue;
            fa[v][0] = u, d[v] = d[u] + 1;
            for (int i = 1; i < M; i++) fa[v][i] = fa[fa[v][i - 1]][i - 1];
            dfs(v);
        }
    }
    void init(int n) {
        for (int i = 1, u, v; i < n; i++) {
            scanf("%d%d", &u, &v);
            e[u].push_back(v);
            e[v].push_back(u);
        }
    }
    int lca(int u, int v) {
        if (d[u] < d[v]) swap(u, v);
        for (int i = M - 1; ~i; i--)
            if (d[fa[u][i]] >= d[v]) u = fa[u][i];
        if (u == v) return u;
        for (int i = M - 1; ~i; i--)
            if (fa[u][i] != fa[v][i]) u = fa[u][i], v = fa[v][i];
        return fa[u][0];
    }
} ex, ey;
int main() {
    int n, addx, addy;
    scanf("%d%d%d", &n, &addx, &addy);
    ex.init(n);
    ey.init(n);
    ex.dfs(addx);
    ey.dfs(addy);
    queue<int> que;
    que.push(addx);
    int ans = 0;
    while (!que.empty()) {
        int u = que.front();
        que.pop();
        if (ex.d[u] >= ey.d[u]) continue;
        int f = ex.fa[u][0], tmp = ey.lca(u, f);
        if (ey.d[u] + ey.d[f] - 2 * ey.d[tmp] > 2) {
            puts("-1");
            return 0;
        }
        ans = max(ans, ey.d[u] << 1);
        for (auto v : ex.e[u])
            if (v != f) que.push(v);
    }
    printf("%d\n", ans);
    return 0;
}

