inline int read(){
	int ret=0;bool f=0;char ch=nc();
	while(ch>'9'||ch<'0') f^=ch=='-',ch=nc();
	while(ch<='9'&&ch>='0') ret=ret*10+ch-'0',ch=nc();
	return f?-ret:ret;
}
void DFS(int x){fr(B,i,x) if(B.son[i]^fa[x]) dst[B.son[i]]=dst[x]+1,fa[B.son[i]]=x,DFS(B.son[i]);}
inline void BFS(){
	int hed=0,til=1;Q[vis[X]=1]=X;
	while(hed<til) if(Dst[Q[++hed]]<dst[Q[hed]]) fr(A,i,Q[hed])
	if(!vis[A.son[i]]) vis[A.son[i]]=1,Dst[A.son[i]]=Dst[Q[hed]]+1;
}
inline bool check(int x,int y){
	if(dst[x]>dst[y]){int t=x;x=y;y=t;}
	if(dst[y]-dst[x]==2) return fa[fa[y]]==x;
	if(dst[y]-dst[x]==1) return fa[y]==x;
	if(dst[y]-dst[x]==0) return fa[x]==fa[y];
