#define MAXN 200010
using namespace std;

struct edge{
	int to,next;
	edge(int _to=0,int _next=0):to(_to),next(_next){}
}e[MAXN<<1],e2[MAXN<<1];

int n,x1,x2;
int g[MAXN],nume;
int g2[MAXN],nume2;
int dep[MAXN],size[MAXN],son[MAXN],top[MAXN],pre[MAXN],w[MAXN],pt[MAXN],numw;
int pre2[MAXN],h[MAXN],dep2[MAXN];
bool flag;
int ans;

void addEdge(int u,int v){
	e[nume]=edge(v,g[u]);
	g[u]=nume++;
}

void addEdge2(int u,int v){
	e2[nume2]=edge(v,g2[u]);
	g2[u]=nume2++;
}

void dfs(int x,int p){
	size[x]=1; son[x]=0;
	pre[x]=p;
	for(int i=g[x];~i;i=e[i].next)
		if(e[i].to^p){
			dep[e[i].to]=dep[x]+1;
			dfs(e[i].to,x);
			size[x]+=size[e[i].to];
			if(size[e[i].to]>size[son[x]])
				son[x]=e[i].to;
		}
}

void dfs2(int x,int p){
	pt[w[x]=++numw]=x;
	if(son[x]){
		top[son[x]]=top[x];
		dfs2(son[x],x);
	}
	for(int i=g[x];~i;i=e[i].next)
		if(e[i].to!=p && e[i].to!=son[x]){
			top[e[i].to]=e[i].to;
			dfs2(e[i].to,x);
		}
}

int getLCA(int x,int y){
	while(top[x]^top[y]){
		if(dep[top[x]]<dep[top[y]]) swap(x,y);
		x=pre[top[x]];
	}
	return dep[x]<dep[y]?x:y;
}

int getDis(int x,int y){
	return dep[x]+dep[y]-2*dep[getLCA(x,y)];
}

int goUp(int x,int d){
	while(dep[x]-dep[top[x]]+1<d){
		d-=dep[x]-dep[top[x]]+1;
		x=pre[top[x]];
	}
	return pt[w[x]-d];
}

int step(int x,int y){
	int lca=getLCA(x,y);
	if(lca!=x) return pre[x];
	return goUp(y,dep[y]-dep[x]-1);
}

void dfs3(int x,int p){
	pre2[x]=p;
	for(int i=g2[x];~i;i=e2[i].next)
		if(e2[i].to^p){
			int y=e2[i].to;
			int dis=getDis(x,y);
			if(y==h[x] || x==h[x]) continue;
			if(dis>=3 && x!=h[x]) flag=1;
			h[y]=step(h[x],y);
			dep2[y]=dep2[x]+1;
			dfs3(y,x);
		}
	int res=getDis(h[x],x)*2+dep2[x]*2;
	ans=max(ans,res);
}

int main(){
#ifdef DEBUG
	freopen("E.in","r",stdin);
#endif
	memset(g,-1,sizeof g);
	memset(g2,-1,sizeof g2);
	scanf("%d%d%d",&n,&x1,&x2);
	for(int i=1;i<n;i++){
		int u,v;
		scanf("%d%d",&u,&v);
		addEdge2(u,v);
		addEdge2(v,u);
	}
	for(int i=1;i<n;i++){
		int u,v;
		scanf("%d%d",&u,&v);
		addEdge(u,v);
		addEdge(v,u);
	}
	dfs(1,0);
	top[1]=1;
	dfs2(1,0);
	h[x1]=x2;
	dfs3(x1,0);
	if(flag) puts("-1");
	else printf("%d\n",ans);
	return 0;
}

#include<bits/stdc++.h>
using namespace std;

struct LCA{
	vector<vector<int> > g;
	const int MAX_LOG = 22;
	vector<vector<int> > lcc;
	vector<int> dep;
	vector<int> myr;
private:
	void init2(){
		int n = g.size();
		for (int i = 0; i + 1 < MAX_LOG; i++){
			for (int j = 0; j < n; j++){
				if (lcc[i][j] == -1){
					lcc[i + 1][j] = -1;
					continue;
				}
				lcc[i + 1][j] = lcc[i][lcc[i][j]];
			}
		}
	}
public:
	int lca(int a, int b){
		if (dep[a] < dep[b]){
			swap(a, b);
		}
		for (int i = 0; i < MAX_LOG; i++){
			if (((dep[a] - dep[b]) >> i) & 1){
				a = lcc[i][a];
			}
		}
		if (a == b){
			return a;
		}
		for (int i = MAX_LOG - 1; i >= 0; i--){
			if (lcc[i][a] != lcc[i][b]){
				a = lcc[i][a];
				b = lcc[i][b];
			}
		}
		return lcc[0][a];
	}
private:
	int flag_r;
	inline void dfs(int b, int pr = -1, int d = 0){
		for (auto &i : g[b]){
			if (i == pr)continue;
			dfs(i, b, d + 1);
		}
		dep[b] = d;
		lcc[0][b] = pr;
		myr[b] = flag_r;
	}
public:

	void init(vector<vector<int> > &tree, int root = 0){
		g = tree;
		myr.resize(tree.size(), -1);
		flag_r = root;
		lcc.resize(MAX_LOG, vector<int>(g.size(), 0));
		dep.resize(g.size());
		dfs(root);
		init2();
	}
	void init(vector<vector<int> > &tree, vector<int> &root){
		g = tree;
		myr.resize(tree.size(), -1);
		lcc.resize(MAX_LOG, vector<int>(g.size(), 0));
		dep.resize(g.size());
		for (int &i : root){
			if (myr[i] == -1){
				flag_r = i;
				dfs(i);
			}
		}
		init2();
	}
	int dist(int a, int b){ 
		if (myr[a] != myr[b]){
			return -1;
		}
		int lc = lca(a, b);
		return dep[a] + dep[b] - 2 * dep[lc];
	}
	int go(int a, int b){
		for (int i = 0; i < MAX_LOG; i++){
			if ((b >> i) & 1){
				a = lcc[i][a];
			}
		}
		return a;
	}
};

#define MAX 200002

int n;

int x;
int y;

vector<vector<int> > sigma;
vector<vector<int> > sugim;

LCA sigmaL;
LCA sugimL;

bool win[MAX];

vector<vector<int> > vv;
queue<int> q;
