using namespace std;
const int NN = 2e5 + 5;
struct E {
	int v, nxt;
	E(int _v=0, int _nxt=0) : v(_v), nxt(_nxt) {}
};
struct Graph {
	int head[NN], totE;
	E edge[NN*2];
	void AddEdge(int u, int v) {
		edge[++totE] = E(v, head[u]);
		head[u] = totE;
	}
} a, b;
int N, X, Y, da[NN], db[NN], fab[NN];
bool save[NN];
void Dfs1(int u, int fr) {
	fab[u] = fr;
	db[u] = db[fr] + 1;
	for (int p = b.head[u]; p; p = b.edge[p].nxt) {
		int v = b.edge[p].v; // 
		if (v != fr)
			Dfs1(v, u);
	}
}
bool inf, edgetag[NN];
void Dfs2(int u, int fr) {
	da[u] = da[fr] + 1;
	if (da[u] >= db[u]) ;
	else save[u] = 1;
	if (fr != 0) {
		int x = u, y = fr;
		if (db[x] < db[y]) swap(x, y);
		if (fab[x] == fab[y] || fab[x] == y || fab[fab[x]] == y) ;
		else {
			inf = 1;
			edgetag[u] = 1;
		}
	}
	for (int p = a.head[u]; p; p = a.edge[p].nxt) {
		int v = a.edge[p].v;
		if (v != fr) {
			Dfs2(v, u);
			if (edgetag[v] && (save[u] || save[v]))
				inf = 1;
		}
	}
}
int main() {
	scanf("%d%d%d", &N, &X, &Y);
	for (int i = 1; i < N; ++i) {
		int u, v;
		scanf("%d%d", &u, &v);
		a.AddEdge(u, v);
		a.AddEdge(v, u);
	}
	for (int i = 1; i < N; ++i) {
		int u, v;
		scanf("%d%d", &u, &v);
		b.AddEdge(u, v);
		b.AddEdge(v, u);
	}
	Dfs1(Y, 0);
	Dfs2(X, 0);
	if (inf) printf("-1\n");
	else {
		int ans = 0;
		for (int i = 1; i <= N; ++i)
			if (save[i])
				ans = max(ans, db[i]-1);
		ans *= 2;
		printf("%d\n", ans);
	}
	return 0;
}

