const int maxn = 2e5 + 10;

int ans, n, rr, rb, dr[maxn], db[maxn], q[maxn], par[maxn], fnt, rar;
bool f[maxn];
std::vector<int> gr[maxn], gb[maxn];

int main() {
	scanf("%d%d%d", &n, &rr, &rb);
	for (int i = 1; i < n; ++i) {
		int u, v;
		scanf("%d%d", &u, &v);
		gr[u].push_back(v);
		gr[v].push_back(u);
	}
	for (int i = 1; i < n; ++i) {
		int u, v;
		scanf("%d%d", &u, &v);
		gb[u].push_back(v);
		gb[v].push_back(u);
	}
	memset(db, -1, sizeof db);
	fnt = rar = 0;
	db[q[rar++] = rb] = 0;
	while(fnt != rar) {
		int u = q[fnt++];
		for (int &v: gb[u]) {
			if(!~db[v]) {
				par[v]= u;
				db[q[rar++] = v] = db[u] + 1;
			}
		}
	}
	for (int u = 1; u <= n; ++u) {
		for (int i = 0; i < (int) gr[u].size(); ++i) {
			int v = gr[u][i];
			if(v == par[u] || u == par[v] || v == par[par[u]] || u == par[par[v]] || par[u] == par[v]) {
			}
			else {
				f[u] = 1;
				break;
			}
		}
	}
	memset(dr, -1, sizeof dr);
	fnt = rar = 0;
	dr[q[rar++] = rr] = 0;
	while(fnt != rar) {
		int u = q[fnt++];
		for (int &v: gr[u]) {
			if(!~dr[v] && dr[u] + 1 < db[v]) {
				dr[q[rar++] = v] = dr[u] + 1;
			}
		}
	}
	for (int u = 1; u <= n; ++u) {
		if(~dr[u]) {
			if(dr[u] < db[u] && f[u]) {
				puts("-1");
				return 0;
			}
			ans = std::max(ans, db[u]);
		}
	}
	printf("%d\n", ans << 1);
	return 0;
}

