const int LGN = 18;
int up[LGN][N];
int D[N];

void DFS(int x, int p = -1) {
    D[x] = (p == -1) ? 0 : D[p] + 1;
    up[0][x] = (p == -1) ? x : p;
    for (int d = 1; d < LGN; d++)
        up[d][x] = up[d - 1][up[d - 1][x]];
    for (int y : E[x]) {
        if (y != p)
            DFS(y, x);
    }
}

inline int lca(int a, int b) {
    if (D[a] > D[b]) {
        swap(a, b);
    }
    for (int d = LGN - 1; d >= 0; d--)
        if (D[up[d][b]] >= D[a])
            b = up[d][b];
    if (a == b)
        return a;
    for (int d = LGN - 1; d >= 0; d--)
        if (up[d][a] != up[d][b])
            a = up[d][a], b = up[d][b];
    return up[0][a];
}

inline int dist(int a, int b) {
    int l = lca(a, b);
    return D[a] + D[b] - 2 * D[l];
}

bool infinite(int x) {
    for (int y : F[x]) {
        if (dist(x, y) >= 3)
            return true;
    }
    return false;
}

bool isinfinite = false;
int best = 0;

int s, t;


