		for(int na:Ga[a]) if(!vis[na]){
			if(distB(a,na)>=3){
				return -1;
			}
			vis[na]=1;
			if(b==na) continue;//?
			int nb=toclose(b,na);
			if(nb==na) continue;
			que.push(P(na,nb));
		}
	}
	return max(ans,d*2);
}
