{
    size[x] = 1;
    for(int i = hb[x]; i; i = edge[i].next)
    {
        int y = edge[i].to; if(y == fa[x]) continue;
        d[y] = d[x]+1, fa[y] = x, dfs(y), 
        size[x] += size[y], son[x] = size[son[x]]>size[y]?son[x]:y; 
    }
}
int lca(int x, int y)
{
    while(top[x] != top[y])
    {
        if(d[top[x]] < d[top[y]]) swap(x, y);
        x = fa[top[x]];
    }
    return d[x]<d[y]?x:y;
}
