#define Add(x, y) x = (x + (y)) % mod
#define Mult(x, y) x = (x * (y)) % mod

struct edge{int to, cost;};

class Graph
{
public:
    int N;
    vector<vector<edge>> G;
    vec par, d;

    Graph(int N): N(N){
        G = vector<vector<edge>>(N, vector<edge>(0));
        par.resize(N);
        d.resize(N);
    }

    void add_Directed_edge(int from, int to, int cost = 1){
        G[from].push_back(edge({to, cost}));
    }

    void add_Undirected_edge(int v1, int v2, int cost = 1){
        add_Directed_edge(v1, v2, cost);
        add_Directed_edge(v2, v1, cost);
    }

    void dfs(int v, int p = -1){
        par[v] = p;
        if(p == -1) d[v] = 0;
        else{
            par[v] = p;
            d[v] = d[p] + 1;
        }
        for(auto e: G[v]) if(e.to != p) dfs(e.to, v);
    }

    void calcans(int v, int &ans, vec &D, vec &Par){
        Max(ans, 2 * D[v]);
        if(D[v] <= d[v]) return;
        for(auto e: G[v]) if(e.to != par[v]){
            if(v != Par[e.to] && e.to != Par[v] && Par[v] != Par[e.to] && !(Par[v] != -1 && Par[Par[v]] == e.to) && !(Par[e.to] != -1 && Par[Par[e.to]] == v)) ans = INF;
            calcans(e.to, ans, D, Par);
        } 
    }

};

signed main(){

    int N, X, Y; cin >> N >> X >> Y;
    X--; Y--;
    Graph R(N), B(N);
    int a, b;
    REP(i, N - 1){
        cin >> a >> b;
        R.add_Undirected_edge(a - 1, b - 1);
    }
    REP(i, N - 1){
        cin >> a >> b;
        B.add_Undirected_edge(a - 1, b - 1);
    }

    R.dfs(X);
    B.dfs(Y);
    vec D = B.d, Par = B.par;
    int ans = 0;
    R.calcans(X, ans, D, Par);
    Ans(ans < INF, ans, -1);

    return 0;
}

