int n,x,y,dep[maxn],fa[maxn],l[maxn],r[maxn],cnt,u1[maxn],v1[maxn],dist[maxn],ans;
bool vis[maxn],islong[maxn];
struct node { int v; node *nxt; } edge[maxn*4],*head[2][maxn],*ncnt;
queue<int> q;
void addedge(int x,int u,int v)
{
	ncnt++;
	ncnt->v=v,ncnt->nxt=head[x][u];
	head[x][u]=ncnt;
}
void dfs(int u)
{
	l[u]=++cnt;
	for(node *p=head[1][u];p;p=p->nxt)
	{
		int v=p->v;
		if(v==fa[u]) continue;
		fa[v]=u,dep[v]=dep[u]+1;
		dfs(v);
	}
	r[u]=++cnt;
}
bool check(int x,int y)
{
	if(l[x]>l[y]) swap(x,y);
	if(l[x]<=l[y]&&r[y]<=r[x]) return dep[y]-dep[x]>2;
	else return fa[x]!=fa[y];
}
void bfs()
{
	while(!q.empty()) q.pop();
	vis[x]=true; q.push(x);
	while(!q.empty())
	{
		int u=q.front(); q.pop();
		for(node *p=head[0][u];p;p=p->nxt)
		{
			int v=p->v;
			if(vis[v]) continue;
			dist[v]=dist[u]+1;
			if(dist[v]<dep[v]) { vis[v]=true; q.push(v); }
		}
	}
}
