		memset(vis,0,sizeof(vis));
		memset(islong,0,sizeof(islong));
		memset(head,0,sizeof(head));
		ncnt=&edge[0];
		for(int i=1;i<n;i++) { read(u1[i]); read(v1[i]); }//scanf("%d%d",&u1[i],&v1[i]);
		for(int i=1;i<n;i++)
		{
			int u,v;
			read(u); read(v);//scanf("%d%d",&u,&v);
			addedge(1,u,v); addedge(1,v,u);
		}
		fa[y]=dep[y]=0; dfs(y);
		for(int i=1;i<n;i++)
			if(check(u1[i],v1[i])) islong[u1[i]]=islong[v1[i]]=true;
			else { addedge(0,u1[i],v1[i]); addedge(0,v1[i],u1[i]); }
		dist[x]=0; bfs();
		int ans=0;
		for(int i=1;i<=n&&ans!=-1;i++)
			if(vis[i]&&islong[i]) ans=-1;
			else if(vis[i]) ans=max(ans,dep[i]*2);
		printf("%d\n",ans);
	}
}

