#define forn(i, n) for (int i = 0; i < (int)(n); ++i)
typedef long long LL;
typedef pair<int, int> PII;

int n;
vector<int> r[200000], b[200000];
int x, y;
int dist[200000], can[200000];
queue<int> q;
vector<PII> all;
set<PII> be;
set<int> se[200000];

int main() {
    scanf("%d", &n);
    scanf("%d%d", &x, &y), --x, --y;
    forn(i, n - 1) {
        int from, to;
        scanf("%d%d", &from, &to), --from, --to;
        r[from].pb(to);
        r[to].pb(from);
    }
    forn(i, n - 1) {
        int from, to;
        scanf("%d%d", &from, &to), --from, --to;
        b[from].pb(to);
        b[to].pb(from);
        be.insert(mp(min(from, to), max(from, to)));
        se[from].insert(to);
        se[to].insert(from);
    }
    memset(dist, -1, sizeof dist);
    dist[y] = 0;
    q.push(y);
    while (!q.empty()) {
        int v = q.front();
        q.pop();
        for (int to : b[v]) if (dist[to] == -1) {
            dist[to] = dist[v] + 1;
            q.push(to);
        }
    }
    memset(can, -1, sizeof can);
    can[x] = 0;
    q.push(x);
    while (!q.empty()) {
        int v = q.front();
        q.pop();
        for (int to : r[v]) if (can[to] == -1 && can[v] + 1 < dist[to]) {
            can[to] = can[v] + 1;
            q.push(to);
        }
    }
    forn(i, n) if (can[i] != -1) {
        for (int to : r[i]) {
            all.pb(mp(min(i, to), max(i, to)));
        }
    }
    sort(all.begin(), all.end());
    all.erase(unique(all.begin(), all.end()), all.end());
    for (PII x : all) {
        if (be.count(x)) {
            continue;
        }
        int mn = x.first;
        int oth = x.second;
        if (b[x.second].size() < b[x.first].size()) {
            swap(mn, oth);
        }
        bool ok = true;
        for (int to : b[mn]) {
            if (se[to].count(oth)) {
                ok = false;
                break;
            }
        }
        if (ok) {
            cout << -1 << endl;
            return 0;
        }
    }
    int ans = 0;
    forn(i, n) if (can[i] != -1) {
        ans = max(ans, dist[i]);
        for (int to : r[i]) {
            ans = max(ans, dist[to]);
        }
    }
    cout << 2 * ans << endl;
    return 0;
}

