	scanf("%d%d%d", &n, &a, &b);
	for(int i = 2;i <= n;i++) lg[i] = lg[i - 1] + (1 << (lg[i - 1] + 1) == i);
	for(int i = 1;i < n;i++){
		scanf("%d%d", &x, &y);
		v1[x].push_back(y), v1[y].push_back(x);
	}
	for(int i = 1;i < n;i++){
		scanf("%d%d", &x, &y);
