int dist[N], papa[N];
vector<int> graph[N];

int sp[N];
vector<int> gr[N];
int ans;

void dfs(int v, int p, int d)
{
    papa[v] = p;
    dist[v] = d;
    for (auto u : graph[v]) if (u != p)
    {
        dfs(u, v, d + 1);
    }
}

int dista(int v, int u)
{
    if (v == papa[u]) return 1;
    if (u == papa[v]) return 1;
    if (papa[v] == papa[u]) return 2;
    if (papa[papa[v]] == u) return 2;
    if (papa[papa[u]] == v) return 2;
    return 3;
}

void dfs2(int v, int p, int d)
{
    if (d >= dist[v]) return;
    if (sp[v])
    {
        cout << -1;
        exit(0);
    }
    ans = max(ans, dist[v]);
    for (auto u : gr[v]) if (u != p)
    {
        dfs2(u, v, d + 1);
    }
}

